    if (idx  L->length) {
        return ERROR;
    }

    LPNode p = L->header;
    for (int i = 0; i Next;
    }

    // 返回这个元素给 e
    e = p->element;

    return OK;
}

获取元素存放的位置：
// 位置是 idx + 1
int Locate(LHeader L, Element e) {
    int idx = 0;
    LPNode p = L->header;

    while (p->Next) {
        idx++;
        // 如果找到了第一个相同的元素，就终止循环
        if (!memcmp(&(p->element), &e, sizeof(e))) {
            break;
        }
        p = p->Next;
    }

    // 如果没找到
    if (!p->Next) {
        return ERROR;
    }

    return idx;
}

