Status PriorElement(LHeader L, Element current_element, Element &prior_element) {
    LPNode q, p = L->header;

    // 如果指定元素是第一个元素，则返回 ERROR，因为没有前驱
    if (!memcmp(&(p->element), &current_element, sizeof(current_element))) {
        return ERROR;
    }

    // 遍历线性表
    while (p->Next) {
        q = p;
        p = p->Next;
        // 找到第一个相等的元素
        if (!memcmp(&(p->element), &current_element, sizeof(current_element))) {
            // 返回前驱
            prior_element = q->element;
            break;
        }
    }

    // 如果没找到
    if (!p->Next) {
        return ERROR;
    }

    return OK;
}

