    LPNode s = (LPNode) malloc(sizeof(LNode));
    s->element = e;
    s->Next = p;

    // 如果是首元结点
    if (idx == 1) {
       L->header = s;
       s->Prev = NULL;
    } 
    // 其他结点
    else {
        // q -> s -> p
        q->Next = s;
        s->Prev = q;
        p->Prev = s;
    }

    L->length++;

    return OK;
}

在双向链表中删除一个元素的算法如下：
// 删除 idx - 1 上的元素
Status DeleteElementByIndex(LHeader &L, int idx, Element &e) {
    if (idx  L->length) {
        return OK;
    }
    LPNode p = L->header;
    // 顺序查找，找到目标索引的元素
    for (int i = 0; p->Next && i Next;
    }    

    LPNode q = p->Next;
    // 返回被删除的元素
    e = p->element;
    // 被删除元素的直接后继元素变为被删除元素直接前驱元素的直接后继元素
    p->Next = q->Next;
    // 被释放的元素的下一个元素的直接前驱变为被删除元素的直接前驱
    q->Next->Prev = p;
    free(q);

    L->length--;

    return OK;
}

